from typing import Listfrom pathlib import Pathimport shutilclass Parser:    def __init__(self):        self.extensions: List[str] = []        def valid_extensions(self, extension):        if extension in self.extensions:            return True        else:            return False            def parse(self, path, source, dest):        raise NotImplementedError            def read(self, path):        with open(path) as file:            file.read()                def write(self, path, dest, content, ext=".html"):        full_path = self.dest / path.with_suffix(ext).name         with open(full_path) as file:            file.write(content)                def copy(self, path, source, dest):        shutil.copy2(path, dest / path / source)            class ResourceParser:        extensions = [".jpg", ".png", ".gif", ".css", ".html"]        